import { GoPlus, ErrorCode } from '@goplus/sdk-node';

async function test() {
  // input decode
  let decodeRes = await GoPlus.inputDecode(
    '1',
    '0x4cc8aa0c6ffbe18534584da9b592aa438733ee66',
    '0xa0712d680000000000000000000000000000000000000000000000000000000062fee481',
    30,
  );

  if (decodeRes.code !== ErrorCode.SUCCESS) {
    console.error(decodeRes.message);
  } else {
    console.log(decodeRes);
  }

  // input decode with signer
  let signer = '0x0000000000000000000000000000000000000000';
  let data =
    '';
  // input decode
  let decodeRes2 = await GoPlus.inputDecodeWithOpts('56', '0x10ED43C718714eb63d5aA57B78B54704E256024E', data, {
    signer: signer,
    timeout: 30,
  });
  if (decodeRes2.code != ErrorCode.SUCCESS) {
    console.error(decodeRes2.message);
  } else {
    console.log(decodeRes2.result.risk);
  }
}

test();
